$().ready(function() {
    $("#hcn").validate({
        onfocusout: false,
        onkeyup: false,
        onclick: false,
        rules: {
            "long": {
                required: true,
                number: true
            },
            "large": {
                required: true,
                number: true
            },
        },
        messages: {
            "long": {
                required: "Không được để trống dữ liệu",
                number: "Cần nhập dữ liệu là số",
            },
            "large": {
                required: "Không được để trống dữ liệu",
                number: "Cần nhập dữ liệu là số",
            },
        }
    });
});
function validate(){
   long= $('#numa').val();
   large= $('#numb').val();
   if(long && large){
       Result='Chu vi: ' + (parseInt(long)+parseInt(large))*2 + 'm'+'<br>'+'Diện tích: ' +
           + (parseInt(long)*parseInt(large)) + 'm<sup>2</sup>';
   }
    $('#result').html(Result);
    return false
}