$().ready(function() {
    $("#taixiu").validate({
        onfocusout: false,
        onkeyup: false,
        onclick: false,
        rules: {
            "num": {
                required: true,
                number: true
            }
        },
        messages: {
            "num": {
                required: "Không được để trống dữ liệu",
                number: "Cần nhập dữ liệu là số",
            },
        }
    });
});
function validate(){
    num=parseInt($('#num').val());
    if(num){
        odd='Các số chẵn từ 1-'+num+' là :';
        even='Các số lẻ từ 1-'+num+' là :';
        for(i=1;i<=num;i++){
            if( i % 2 == 0 ){
                odd +=' '+i;
            }else {
                even += ' '+i;
            }

        }
    }
    $('#oddnum').html(
        odd
    );
    $('#evennum').html(
        even
    );
    return false
}